import React, { useContext, useEffect } from "react";
import { useParams } from "react-router-dom";
import { AnimeContexte } from "../contexte/AnimeContexte";

const Anime = () => {
  const { fetchById, animesbyId } = useContext(AnimeContexte);
  const { id } = useParams();

  useEffect(() => {
    fetchById(id);
    console.log("animbyid", animesbyId);
  }, []);
  return <div>Anime {id}</div>;
};

export default Anime;
