import React, { useContext, useEffect, useRef, useLayoutEffect } from "react";
import { useParams } from "react-router-dom";
import { AnimeContexte } from "../contexte/AnimeContexte";

const Anime = () => {
  const { id } = useParams();
  const { loading, fetchById, animesbyId, clearAnimebyId } =
    useContext(AnimeContexte);
  console.log(id);
  //   useEffect(() => {
  //     fetchById(id);
  //     console.log("ss");
  //   }, []);

  const useDidMountEffect = (func, deps) => {
    const didMount = useRef(false);

    useEffect(() => {
      if (didMount.current) fetchById(id);
      else didMount.current = true;
    }, deps);
  };

  return (
    <>
      {loading ? (
        <p>uu</p>
      ) : (
        <div className="cardAnime">
          {animesbyId.attributes.coverImage == undefined &&
          animesbyId.attributes.coverImage == null ? (
            <p className="alertImagecover">Pas d'image</p>
          ) : (
            <img
              className="imagePoster"
              src={animesbyId.attributes.coverImage.original}
              alt="yy"
            />
          )}
          <img
            className="imageCover"
            src={animesbyId.attributes.posterImage.small}
            alt="yy"
          />
          <p>ratingRank: {animesbyId.attributes.ratingRank}</p>
          <p>{animesbyId.attributes.titles.en}</p>
        </div>
      )}
    </>
  );
};

export default Anime;
