import React, { useContext, useEffect } from "react";
import { Link } from "react-router-dom";
import { AnimeContexte } from "../contexte/AnimeContexte";

const Home = () => {
  const { loading, fetchAnime, animes, animesbysearch } =
    useContext(AnimeContexte);
  useEffect(() => {
    fetchAnime();
  }, []);

  return (
    <div>
      {loading ? (
        <p>uu</p>
      ) : (
        <>
          {animes.map((anime) => (
            <div key={anime.id} style={{ border: "2px solid red" }}>
              <p>titre japonais: {anime.attributes.titles.ja_jp}</p>
              <p>titre: {anime.attributes.titles.en}</p>
              <p>Age recommender: {anime.attributes.ageRating}</p>
              <p>Age recommender: {anime.attributes.ageRatingGuide}</p>
              <p>startDate: {anime.attributes.startDate}</p>
              <p>ratingRank: {anime.attributes.ratingRank}</p>
              <Link to={`/anime/${anime.id}`}>Plus</Link>
            </div>
          ))}
        </>
      )}
    </div>
  );
};

export default Home;
