import { AnimeContexte } from "../contexte/AnimeContexte";

const api = "https://kitsu.io/api/edge/";

const Add = () => {
  const { data, setData, page, setPage, hasMore, setHasMore } =
    useContext(AnimeContexte);

  useEffect(() => {
    fetchData();
  }, [page]);

  const numberOfElementsToRemove = 4;
  const startIndex = data.length - numberOfElementsToRemove;

  const handleLoadMore = () => {
    setPage(page + 5);
    console.log(page);
  };
  const handleLoadMore2 = () => {
    data.splice(startIndex, numberOfElementsToRemove);
  };

  return (
    <div>
      <ul>
        {data.map((item) => (
          <li key={item.id}>{`${item.attributes.titles.en} id ${item.id}`}</li>
        ))}
      </ul>
      {loading && <div>Loading...</div>}
      {hasMore &&
        window.addEventListener("scroll", () => {
          if (
            window.innerHeight + window.scrollY >=
            document.body.offsetHeight
          ) {
            handleLoadMore();
          }
          // else if (
