import React, { useState, useEffect, useContext } from "react";
import { AnimeContexte } from "../contexte/AnimeContexte";

const api = "https://kitsu.io/api/edge/";

const Add = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(false);
  const { fetchAnime, animes, animesbysearch } = useContext(AnimeContexte);

  useEffect(() => {
    setLoading(true);
    const fetchData = async () => {
      const response = await fetch(
        `${api}anime?page[limit]=5&page[offset]=${page}`
      );
      const result = await response.json();
      setData(data.concat(result.data));
      setHasMore(result.data.length > 0);
      setLoading(false);
    };
    fetchData();
  }, [page]);

  const handleLoadMore = () => {
    setPage(page + 5);
  };
  const handleLoadMore2 = () => {
    setPage(page - 5);
  };

  return (
    <div>
      <ul>
        {data.map((item) => (
          <li key={item.id}>{`${item.attributes.titles.en} id ${item.id}`}</li>
        ))}
      </ul>
      {loading && <div>Loading...</div>}
      {hasMore &&
        window.addEventListener("scroll", () => {
          if (
            window.innerHeight + window.scrollY >=
            document.body.offsetHeight
          ) {
            handleLoadMore();
          }
          // else if (
          //   window.innerHeight + window.scrollY <
          //   document.body.offsetHeight
          // ) {
          //   handleLoadMore2();
          // }
        })}
      {/* <button>rrrrrrrr</button> */}
    </div>
  );
};

export default Add;
