import React, { useState, useEffect, useContext } from "react";
import { AnimeContexte } from "../contexte/AnimeContexte";

const Add = () => {
  const {
    dataTable,
    setDataTable,
    page,
    setPage,
    hasMore,
    setHasMore,
    loading2,
    setLoading2,
  } = useContext(AnimeContexte);
  const Url = "https://kitsu.io/api/edge/";

  useEffect(() => {
    const fetchData = async () => {
      setLoading2(true);
      try {
        const response = await fetch(
          `${Url}anime?page[limit]=5&page[offset]=${page}`
        );
        const result = await response.json();
        setDataTable(dataTable.concat(result.data));
        setHasMore(result.data.length > 0);
        setLoading2(false);
        console.log(" ttt", dataTable);
      } catch (error) {
        console.log(error);
      }
      setLoading2(false);
    };
    fetchData();
  }, [page]);

  const numberOfElementsToRemove = 4;
  const startIndex = dataTable.length - numberOfElementsToRemove;

  const handleLoadMore = () => {
    setPage(page + 5);
    console.log(page);
  };
  const handleLoadMore2 = () => {
    dataTable.splice(startIndex, numberOfElementsToRemove);
  };

  return (
    <div>
      <ul>
        {dataTable.map((item) => (
          <li key={item.id}>{`${item.attributes.titles.en} id ${item.id}`}</li>
        ))}
      </ul>
      {loading2 && <div>Loading...</div>}
      {hasMore &&
        window.addEventListener("scroll", () => {
          if (
            window.innerHeight + window.scrollY >=
            document.body.offsetHeight
          ) {
            handleLoadMore();
          }
          // else if (
          //   window.innerHeight + window.scrollY <
          //   document.body.offsetHeight
          // ) {
          //   handleLoadMore2();
          // }
        })}
      {/* <button>rrrrrrrr</button> */}
    </div>
  );
};

export default Add;
