import React, { useContext, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { AnimeContexte } from "../contexte/AnimeContexte";
import Retour from "../icons/retour.png";
import imageCo from "../icons/co.png";
import Alerte from "../components/Alerte";

const Anime = () => {
  const { id } = useParams();
  const { loading, fetchById, animesbyId, animeFavoris, setAnimeFavoris } =
    useContext(AnimeContexte);
  const navigate = useNavigate();
  const AjoutFavoris = (animeFavoris, id) => {
    animeFavoris.some((anime) => anime.id === id) ? (
      <Alerte />
    ) : (
      setAnimeFavoris([...animeFavoris, animesbyId])
    );
  };
  const Retirer = (id) => {
    console.log(id);
    setAnimeFavoris(animeFavoris.filter((anime) => anime.id !== id));
    console.log(animeFavoris);
  };

  useEffect(() => {
    fetchById(id);
    console.log("useEffect", id);

    console.log("animebyid", animesbyId);
  }, []);

  Object.keys(animesbyId).length === 0 && animesbyId.constructor === Object
    ? fetchById(id)
    : console.log("emptyy");

  return (
    <>
      {!loading ? (
        <div>
          <div className="cardAnime">
            {animesbyId.attributes.coverImage == undefined &&
            animesbyId.attributes.coverImage == null ? (
              <p className="alertImagecover">Pas d'image</p>
            ) : (
              <img
                className="imagePoster"
                src={animesbyId.attributes.coverImage.original}
                alt="yy"
              />
            )}
            <img
              className="imageCover"
              src={animesbyId.attributes.posterImage.small}
              alt="yy"
            />
            <p>ratingRank: {animesbyId.attributes.ratingRank}</p>
            <p>{animesbyId.attributes.titles.en}</p>
            <button className="btn" onClick={() => navigate("/")}>
              <img className="icon" src={Retour} alt="retour" />
              Retourner au catalogue
            </button>
          </div>

          <button onClick={() => AjoutFavoris(animeFavoris, id)}>
            Ajouter aux favoris
          </button>
          <Link to="/anime/favoris">
            <button className="Lesfavoris">
              <p>Voir les favoris</p>
              <img src={imageCo} alt="fav" />
            </button>
          </Link>
          <button onClick={() => Retirer(animesbyId.id)}>
            Retirer de Favoris
          </button>
        </div>
      ) : (
        <p>hhh</p>
      )}

      {/* {loading ? <p>hhh</p> : <AnimeDetai anime={animesbyId} />}</>; */}
    </>
  );
};

export default Anime;
