import React, { useEffect } from "react";
import { useParams } from "react-router-dom";

const Anime = () => {
  const { fetchById, animesbyId } = useContext(AnimeContexte);

  useEffect((x) => {
    fetchById(x);
  }, []);
  const { id } = useParams();
  return <div>Anime {id}</div>;
};

export default Anime;
