import React, { useState, useEffect, useContext } from "react";
import { AnimeContexte } from "../contexte/AnimeContexte";

const api = "https://kitsu.io/api/edge/";

const Add = () => {
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(false);
  const { dataa, setDataa } = useContext(AnimeContexte);

  useEffect(() => {
    setLoading(true);
    const fetchData = async () => {
      const response = await fetch(
        `${api}anime?page[limit]=5&page[offset]=${page}`
      );
      const result = await response.json();
      setDataa(dataa.concat(result.dataa));
      setHasMore(result.data.length > 0);
      setLoading(false);
    };
    fetchData();
  }, [page]);

  const handleLoadMore = () => {
    setPage(page + 5);
  };

  return (
    <div>
      <ul>
        {dataa.map((item) => (
          <li key={item.id}>{`${item.attributes.titles.en} id ${item.id}`}</li>
        ))}
      </ul>
      {loading && <div>Loading...</div>}
      {
        hasMore && (
          <button type="button" onClick={handleLoadMore}>
            Load More
          </button>
        )
        // window.scrollBy(0, window.innerHeight) &&
        // handleLoadMore
      }
    </div>
  );
};

export default Add;
