import React, { useContext, useEffect } from "react";
import { useParams } from "react-router-dom";
import { AnimeContexte } from "../contexte/AnimeContexte";
import AnimeDetai from "../components/AnimeDetai";

const Anime = () => {
  const { id } = useParams();
  const { loading, fetchById, animesbyId } = useContext(AnimeContexte);

  useEffect(() => {
    fetchById(id);
    console.log("useEffect", id);

    console.log("animebyid", animesbyId);
  }, []);

  Object.keys(animesbyId).length === 0 && animesbyId.constructor === Object
    ? fetchById(id)
    : console.log("emptyy");

  console.log(id);
  // console.log("animebyid", animesbyId);

  return (
    <>
      {!loading ? (
        <div>
          <div className="cardAnime">
            {anime.attributes.coverImage == undefined &&
            anime.attributes.coverImage == null ? (
              <p className="alertImagecover">Pas d'image</p>
            ) : (
              <img
                className="imagePoster"
                src={anime.attributes.coverImage.original}
                alt="yy"
              />
            )}
            <img
              className="imageCover"
              src={anime.attributes.posterImage.small}
              alt="yy"
            />
            <p>ratingRank: {anime.attributes.ratingRank}</p>
            <p>{anime.attributes.titles.en}</p>
            rrr
          </div>
          <button onClick={() => navigate("/")}>Retourner au catalogue</button>
          <button onClick={() => AjoutFavoris()}>Ajouter aux favoris</button>
        </div>
      ) : (
        <p>hhh</p>
      )}

      {/* {loading ? <p>hhh</p> : <AnimeDetai anime={animesbyId} />}</>; */}
    </>
  );
};

export default Anime;
