import React, { useContext, useEffect, useState } from "react";
import { AnimeContexte } from "../contexte/AnimeContexte";
import chercher from "../icons/chercher.svg";

const Navbar = () => {
  const { fetchBySearch } = useContext(AnimeContexte);

  const [showAnnee, setShowAnnee] = useState(false);
  const [showAge, setShowAge] = useState(false);
  const [animeSearch, setAnimeSearch] = useState("");
  const [animeAgerate, setAnimeAgerate] = useState("");

  const showAnneefonc = () => {
    setShowAnnee(!showAnnee);
  };
  const showAgefonc = () => {
    setShowAge(!showAge);
  };
  const handleChange = (e) => {
    setAnimeSearch(e.target.value);
  };

  const getAgerate = (x) => {
    setAnimeAgerate(x);
  };
  const Age = [
    { id: 0, rateAge: "R" },
    { id: 1, rateAge: "PG" },
  ];
  const Annee = [
    { id: 0, annee: "2015" },
    { id: 1, annee: "2016" },
    { id: 2, annee: "2017" },
    { id: 3, annee: "2018" },
    { id: 4, annee: "2019" },
    { id: 5, annee: "2020" },
    { id: 6, annee: "2021" },
    { id: 7, annee: "2022" },
    { id: 8, annee: "2023" },
  ];

  // useEffect(() => {
  //   fetchBySearch(animeAgerate);
  // }, [animeAgerate]);

  return (
    <div className="menu">
      <div className="recherche">
        <input type="text" onChange={handleChange} placeholder="Recherche" />
        <img src={chercher} alt="" />
      </div>

      <div className="dropdown">
        <button className="dropbtn" onClick={showAnneefonc}>
          <p>Année</p>
          <i className="arrow down"></i>
        </button>
        <div
          className={showAnnee ? "dropdown-content show" : "dropdown-content"}
        >
          {Annee.map((anee) => (
            <a key={anee.id} href="#">
              {anee.anee}
            </a>
          ))}
        </div>

        <div className="dropdown2">
          <button className="dropbtn" onClick={showAgefonc}>
            <p>Âge recommandé</p>
            <i className="arrow down"></i>
          </button>
          <div
            className={
              showAge ? "age dropdown-content show" : "age dropdown-content"
            }
          >
            {Age.map((age) => (
              <a onClick={() => getAgerate(age.rateAge)} key={age.id} href="#">
                {age.rateAge}
              </a>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
