import React, { useState, useEffect, useContext } from "react";
import { AnimeContexte } from "../contexte/AnimeContexte";

const api = "https://kitsu.io/api/edge/";

const Add = () => {
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(false);
  const {
    fetchAnime,
    fetchData,
    dataTable,
    setDataTable,
    animes,
    animesbysearch,
  } = useContext(AnimeContexte);

  useEffect(() => {
    fetchData();
  }, [page]);

  const numberOfElementsToRemove = 4;
  const startIndex = dataTable.length - numberOfElementsToRemove;

  const handleLoadMore = () => {
    setPage(page + 5);
    console.log(page);
  };
  // const handleLoadMore2 = () => {
  //   setData(data.splice(startIndex, numberOfElementsToRemove));
  // };

  return (
    <div>
      <ul>
        {dataTable.map((item) => (
          <li key={item.id}>{`${item.attributes.titles.en} id ${item.id}`}</li>
        ))}
      </ul>
      {loading && <div>Loading...</div>}
      {hasMore &&
        window.addEventListener("scroll", () => {
          if (
            window.innerHeight + window.scrollY >=
            document.body.offsetHeight
          ) {
            handleLoadMore();
          }
          // else if (
          //   window.innerHeight + window.scrollY <
          //   document.body.offsetHeight
          // ) {
          //   handleLoadMore2();
          // }
        })}
      {/* <button>rrrrrrrr</button> */}
    </div>
  );
};

export default Add;
