import React, { useContext } from "react";
import { AnimeContexte } from "../contexte/AnimeContexte";

const AnimeFavoris = () => {
  const { animeFavoris, setAnimeFavoris } = useContext(AnimeContexte);
  return (
    <div>
      {animeFavoris.length === 0 ? (
        <p>{`Y'a pas de favoris`}</p>
      ) : (
        animeFavoris.map((anime) => (
          <>
            <p>titre: {anime.attributes.titles.en}</p>
            <img
              className="imageCover"
              src={anime.attributes.posterImage.small}
              alt="yy"
            />
          </>
        ))
      )}
      <button>Retirer de Favoris</button>
    </div>
  );
};

export default AnimeFavoris;
