import React, { useState, useEffect, useContext } from "react";
import { AnimeContexte } from "../contexte/AnimeContexte";

const Add = () => {
  const {
    fetchData,
    dataTable,
    setDataTable,
    page,
    setPage,
    hasMore,
    setHasMore,
    loading2,
    setLoading2,
  } = useContext(AnimeContexte);

  useEffect(() => {
    fetchData();
  }, [page]);
  // if (dataTable.length === 0) {
  //   fetchData();
  // }

  const numberOfElementsToRemove = 4;
  const startIndex = dataTable.length - numberOfElementsToRemove;

  const handleLoadMore = () => {
    setPage(page + 5);
    setLoading2(true);
  };
  const handleLoadMore2 = () => {
    dataTable.splice(startIndex, numberOfElementsToRemove);
  };

  return (
    <div>
      {loading2 && <div className="Loadingdata">{<>...</>}</div>}
      {hasMore &&
        window.addEventListener("scroll", () => {
          if (
            window.innerHeight + window.scrollY >=
            document.body.offsetHeight
          ) {
            handleLoadMore();
          }
          // else if (
          //   window.innerHeight + window.scrollY <
          //   document.body.offsetHeight
          // ) {
          //   handleLoadMore2();
          // }
        })}
      {/* <button>rrrrrrrr</button> */}
    </div>
  );
};

export default Add;
