import React, { createContext, useReducer, useState } from "react";
import axios from "axios";
import AnimeReducer from "./AnimeReducer";

export const AnimeContexte = createContext();
const AnimeProvider = ({ children }) => {
  const [loading, setLoading] = useState(true);

  const initialState = {
    animes: [],
    animesbysearch: [],
  };
  const [state, dispatch] = useReducer(AnimeReducer, initialState);

  const fetchAnime = async () => {
    setLoading(true);
    try {
      const response = await axios.get(
        "https://kitsu.io/api/edge/anime?page[limit]=20&page[offset]=0",
        {
          headers: {
            "Content-Type": "application/vnd.api+json",
          },
        }
      );
      console.log("adventur adventure", response.data.data);
      dispatch({
        type: "GET_ANIMES",
        payload: response.data.data,
      });
      console.log("rr", response.data.data);
    } catch (error) {
      console.log(error);
    }
    setLoading(false);
  };

  const fetchBySearch = async (x) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `https://kitsu.io/api/edge/anime?filter[text]=${x}`
      );
      console.log(response.data.data);
      dispatch({
        type: "GET_BYSEARCH",
        payload: response.data.data,
      });
      console.log(response.data.data);
    } catch (error) {
      console.log(error);
    }
    setLoading(false);
  };

  return (
    <AnimeContexte.Provider
      value={{
        loading,
        fetchAnime,
        animes: state.animes,
        fetchBySearch,
        animesbysearch: state.animesbysearch,
      }}
    >
      {children}
    </AnimeContexte.Provider>
  );
};

export default AnimeProvider;
